function getNewCoordinates(dayDelta, startRow, startCol){
						
	dayDelta = parseInt(dayDelta);
	startRow = parseInt(startRow);
	startCol = parseInt(startCol);
	
	var colCnt = $(".fc-week:first td").length;
	
	var deltaRemainder = dayDelta % colCnt;
	var deltaFloor = (dayDelta >= 0) ? Math.floor(dayDelta / colCnt) : Math.ceil(dayDelta / colCnt) ;
	
	var newStartRow = startRow+deltaFloor;
	var newStartCol = startCol+deltaRemainder;
	
	if(newStartCol > (colCnt - 1)){
		
		newStartRow++;
		newStartCol = newStartCol-colCnt;
		
	}
	
	else if(newStartCol < 0){
		
		newStartRow--;
		newStartCol = newStartCol+colCnt;
		
	}	
	
	return newStartRow+'-'+newStartCol;
	
}

function isOverlapping(eventObj, events){
    for(i in events){
		
        if(events[i]._id != eventObj.id){
						
			if(events[i].start.split('-')[0] == eventObj.start.split('-')[0]){
			
				if(!(parseInt(events[i].start.split('-')[1]) > parseInt(eventObj.end.split('-')[1]) || parseInt(events[i].end.split('-')[1]) < parseInt(eventObj.start.split('-')[1]))){
					return true;
				}
				
			}
        }
    }
    return false;
}
							

function dayDiff(d1, d2) { // d1 - d2
	
		var firstNumDate = d1.split('-')[0];
		var lastNumDate = d1.split('-')[1];
		
		var firstNumOrigDate = d2.split('-')[0];
		var lastNumOrigDate = d2.split('-')[1];
		
		var colCnt = $(".fc-week:first td").length;
		
		var rowDelta = (firstNumDate-firstNumOrigDate) * colCnt;	
		var colDelta = (lastNumDate-lastNumOrigDate);
		
		return rowDelta+colDelta;
	
}




